import { Component } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-subtask-list',
  templateUrl: './subtask-list.component.html',
  styleUrls: ['./subtask-list.component.scss']
})
export class SubtaskListComponent {

  tasksForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.tasksForm = this.fb.group({
      tasks: this.fb.array([
        this.createTask('Default Task 1', true),
        this.createTask('Default Task 2', false)
      ])
    });
  }

  createTask(name: string, checked: boolean): FormGroup {
    return this.fb.group({
      name: new FormControl(name),  // Default value for name
      checked: new FormControl(checked) // Default value for checked
    });
  }

  get tasks(): FormArray {
    return this.tasksForm.get('tasks') as FormArray;
  }

  addTask() {
    this.tasks.push(this.createTask('', false)); // Add new task with empty name
  }

  submitTasks() {
    console.log(this.tasksForm.value); // Log the current value of the form
  }


  subTasks = [
    {
      checked: true,
      title: 'Send invoice to collections agency'
    },
    {
      checked: false,
      title: 'Write an email to them to follow-up'
    },
    {
      checked: false,
      title: 'Send paper mail to account'
    },
    {
      checked: true,
      title: `This is my new subtask... I'm autofocused here when I add a subtask`
    }
  ]

  changeCheckStatus(index: number) {
    const taskControl = this.tasks.at(index);
    const checkedControl = taskControl.get('checked');

    if (checkedControl) {
      const currentCheckedValue = checkedControl.value;
      checkedControl.setValue(!currentCheckedValue);
    }
  }

}
<div class="subtask-list">
    <div class="container">
        <div class="top">
            <h5>Subtasks</h5>
            <div class="progress" role="progressbar" aria-label="Basic example" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100">
                <div class="progress-bar" style="width: 75%"></div>
            </div>
            <span>1/4</span>
        </div>
    </div>
    <div class="line-break"></div>

    <div class="container">
        <ul class="list-group">
            <li class="list-group-item">Subtask name</li>

            <!-- {{tasksForm.value | json }} -->

            <!-- <form [formGroup]="tasksForm" (ngSubmit)="submitTasks()">
                <button type="button" (click)="addTask()">Add Task</button>

                <div *ngFor="let task of tasks.controls; let i = index" [formGroupName]="i">
                    <input formControlName="name" placeholder="Enter task name" />
                    <input type="checkbox" formControlName="checked" />
                </div>

                <button type="submit">Submit Tasks</button>
            </form> -->

            <form [formGroup]="tasksForm" (ngSubmit)="submitTasks()">
                <!-- <button type="button" (click)="addTask()">Add Task</button>

                <div *ngFor="let task of tasks.controls; let i = index">
                    <input [formControlName]="i" placeholder="Enter a new task" />
                 </div> -->

                <!-- <button type="submit">Submit Tasks</button> -->

                <!-- <li class="list-group-item" *ngFor="let task of tasks.controls; let i = index" [formGroupName]="i">
                    <div class="checkmark" (click)="changeCheckStatus(i)">
                        <app-icon name="icon-checkmark" *ngIf="checkbox"></app-icon>
                    </div>
                    <div class="title">{{subtask.title}}</div>
                    <input class="title" [formControlName]="name" placeholder="Enter a new task" />

                </li> -->

                <div formArrayName="tasks">
                    <li class="list-group-item" *ngFor="let task of tasks.controls; let i = index" [formGroupName]="i">
                      <div class="checkmark" (click)="changeCheckStatus(i)">
                        <app-icon name="icon-checkmark" *ngIf="task.get('checked')?.value"></app-icon>
                      </div>
                      <input class="title" formControlName="name" placeholder="Enter task name" />
                    </li>
                </div>



                <!-- <li class="list-group-item">
                    <div class="checkmark"></div>
                    <input type="text" placeholder="Writte task name">
                </li> -->
                <li class="list-group-item" (click)="addTask()">
                    <app-icon name="icon-zoomIn"></app-icon>
                    <div class="title">Add subtask</div>
                </li>

            </form>

            <!-- <li *ngFor="let task of tasks.controls">{{ task.value }}</li> -->



          </ul>
    </div>
</div>
