import { Component, Input, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Ticket } from '../../../../models';


@Component({
  selector: 'app-details-main',
  templateUrl: './details-main.component.html',
  styleUrls: ['./details-main.component.scss']
})
export class DetailsMainComponent implements OnInit {

  @Input('name') ticket: Ticket | null = null; 

  constructor() { }

  updateForm: FormGroup = new FormGroup({
    status: new FormControl(false),
    assignee: new FormControl('Brian Griffin'),
    coOwner: new FormControl('Peter Griffin'),
    importance: new FormControl('Very Urgent'),
    custumerName: new FormControl('Customer Na...'),
    invoiceId: new FormControl('19823190'),
  })


  ngOnInit(): void {
    if(this.ticket) this.patchDataToForm();
  }

  patchDataToForm() {
    this.updateForm.patchValue({
      status: this.ticket?.status,
      assignee: this.ticket?.assignee,
      coOwner: this.ticket?.coOwner,
      importance: this.ticket?.importance,
      costumerName: this.ticket?.customerName
    })
  }

}
